package LeeCode;

public class code392 {
    public static void main(String[] args) {
        //给定字符串 s 和 t ，判断 s 是否为 t 的子序列。
        //
        //字符串的一个子序列是原始字符串删除一些（也可以不删除）字符而不改变剩余字符相对位置形成的新字符串。
        // （例如，"ace"是"abcde"的一个子序列，而"aec"不是）。
        //
        //进阶：
        //
        //  如果有大量输入的 S，称作 S1, S2, ... , Sk 其中 k >= 10亿，
        // 你需要依次检查它们是否为 T 的子序列。在这种情况下，你会怎样改变代码？
        String a="leeeeetcode";
        String b= "";
        System.out.println(isSubsequence1(a, b));
    }

    public static boolean isSubsequence(String s, String t) {
        int idx=0,len=s.length();
        if (s.length()==0) return true;
        for (int i = 0; i < t.length(); i++) {
            if (idx<len&&s.charAt(idx)==t.charAt(i)) idx++;//如果一样可以索引后移
            else if (idx>=len) break;
        }
        return idx == s.length() ;
    }

    //这个最快
    public static boolean isSubsequence1(String s, String t) {
        char[] chars = s.toCharArray();
        for (char aChar : chars) {
            int b = t.indexOf(aChar);//如果不存在会返回-1
            if (b != -1) {
                //去掉前面的字符
                t = t.substring(b + 1);
            } else return false;
        }
        return true;
    }
}
